import React, {Component} from "react";
import Header from "./Header"
import View from "./View";
import Modal from "./Modal";

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            articleLists : [],
            clicked : false,
            viewStatus : null,
            currentArticle : "",
            modal : false,
            reloadStatus : false,
            page : 1
        }

        this.changePageStatus = this.changePageStatus.bind(this);
        this.toggleClass = this.toggleClass.bind(this);
        this.toggleView = this.toggleView.bind(this);
        this.renderByListViewMode = this.renderByListViewMode.bind(this);
        this.renderByCardViewMode = this.renderByCardViewMode.bind(this);
        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
        this.changeModalStatusWhenClickOutside = this.changeModalStatusWhenClickOutside.bind(this);
        this.resetArticleLists = this.resetArticleLists.bind(this);
    }

    changePageStatus(articlesToAdd) {
        this.setState({
            articleLists : this.state.articleLists.concat(articlesToAdd)
        });
    }

    toggleClass() {
        const currentState = this.state.clicked;
        this.setState({
            clicked : !currentState
        });
    }

    toggleView(viewStatusFromViewComponent) {
        this.setState({
            viewStatus : viewStatusFromViewComponent
        });
    }

    renderByListViewMode() {
        const newArticle = this.state.articleLists.map((element, index) => {
            return (
                <div key = {index} className = "article-list" onClick = {() => {
                    this.handleOpenModal(element);
                }}>
                    <div className = "list-view-title">{element.title}</div>
                    <div className = "list-view-publisher">{element.publishedAt.slice(0, 10)}</div>
                    <div className = "list-view-source">{element.source.name}</div>
                    <div className = "list-view-author">{element.author}</div>
                </div>
            );
        });

        return newArticle;
    }

    renderByCardViewMode() {
        const newArticle = this.state.articleLists.map((element, index) => {
            return (
                <div key = {index} className = "article-card" onClick = {() => {
                    this.handleOpenModal(element);
                }}>
                    {element.urlToImage ? <img src = {element.urlToImage} className = "card-view-image"/> : <img src = "https://c-lj.gnst.jp/public/img/common/noimage.jpg?20181001050045" className = "card-view-image"/>}
                    <div className = "card-view-content">
                        <div className = "card-view-title">{element.title}</div>
                        <div className = "card-view-author">{element.author}</div>
                    </div>
                </div>
            );
        });

        return newArticle;
    }

    handleOpenModal(data) {
        console.log(data);
        this.setState({
            modal : true,
            currentArticle : data
        })
    }

    resetArticleLists() {
        this.setState({
            articleLists : []
        })
    }

    changeModalStatusWhenClickOutside(data) {
        this.setState({
            modal : data
        })
    }



    handleCloseModal() {
        this.setState({
            modal : false
        })
    }







    render() {
        return (
            <div className = "structure" ref={node => { this.node = node; }}>
                <Header onChangePageState = {this.changePageStatus} resetArticleLists = {this.resetArticleLists}/>
                <View onToggleClass = {this.toggleClass} clickToggleButton = {this.toggleView}/>

                <div className = {!this.state.viewStatus ? "view-list" : "view-card"}>

                    {!this.state.viewStatus ? this.renderByListViewMode() : this.renderByCardViewMode()}
                </div>

                {
                    this.state.modal && (
                        <Modal onClose = {this.handleCloseModal} modalData = {this.state.currentArticle} changeStateModalClickOutside = {this.changeModalStatusWhenClickOutside}/>
                    )
                }
            </div>
        );
    }
}

export default Page;
