import React, { Component } from "react";
import "./List.css";

class List extends Component {
    constructor(props) {
        super(props);
        this.state = {
            checked : []
        };

        this.onClicked = this.onClicked.bind(this);
    }

    onClicked(event) {
        this.setState({
            checked : this.state.checked.concat(event.target.value)
        });
        console.log(this.state);
    }

    render() {
        return (
            <span className = "list">
                <input 
                    type = "checkbox"
                    value = {this.props.name}
                    onClick = {this.onClicked}>
                </input>
                <label>{this.props.name}</label>

            </span>
        );
    }
}

export default List
