import React, {Component} from "react";
import Header from "./Header"
import Articles from "./Articles";
import View from "./View";

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            articleLists : []
        }
        this.handleCreate = this.handleCreate.bind(this);
        this.test = this.test.bind(this);
    }

    handleCreate(articlesToAdd) {
        this.setState({
            articleLists : this.state.articleLists.concat(articlesToAdd)
        });
    }

    test(data) {
        document.querySelector(".newArticle").style.color = "red";
    }

    render() {
        return (
            <div>
                <Header onChangePageState = {this.handleCreate}/>
                {/* view를 이렇게 컴포넌틐로 해도되지만.. 정보가 왔다갔다하는게 어려우면 그냥 여기 때려박아도 될듯한데 */}
                <View onTest = {this.test}/>
                <Articles getArticleLists = {this.state.articleLists}/>
            </div>
        );
    }
}

export default Page;
