import React, { Component } from 'react';
import "./Modal.css";

class Modal extends Component {
    clickOutsideModal = (e) => {
        if(e.target === this.node) {
            this.props.changeStateModalClickOutside(false);
        } 
    }

    render() {
        return (
            <div className = "articleModal" onClick = {this.clickOutsideModal} ref={node => { this.node = node; }}>
                <div className = "modal-header">나는 모달 헤더!</div>
                <div className = "articleModalContent">
                    <div className = "modal-title">{this.props.modalData.title}</div>
                    <div className = "modal-sourceInfo">
                        <div>{this.props.modalData.author}</div>
                        <div>{this.props.modalData.publishedAt}</div>
                        <div>{this.props.modalData.source.name}</div>
                    </div>
                    <div className = "modal-content">
                        <a href = {this.props.modalData.url} target="_blank">{this.props.modalData.urlToImage ? <img src = {this.props.modalData.urlToImage} className = "articleImg"/> : <img src = "https://c-lj.gnst.jp/public/img/common/noimage.jpg?20181001050045" className = "articleImg"/>}
                        {this.props.modalData.content}</a>
                    </div>

                    <div className = "modal-footer">나는 모달 푸터!</div>
                </div>
            </div>
        );
    }
}

export default Modal;
