import React, { Component } from "react";
import "./List.css";

class List extends Component {
    constructor(props) {
        super(props);
        this.state = {
            checked : []
        };
        this.isChecked = this.isChecked.bind(this);
        // this.getgetget = this.getgetget.bind(this);
        // this.onClicked = this.onClicked.bind(this);
    }

    // onClicked(event) {
    //     this.setState({
    //         checked : this.state.checked.concat(event.target.value)
    //     });
    //     console.log(this.state)
    // }
    // getgetget(event) {
    //     this.setState({
    //         checked : this.state.checked.concat(event.target.value)
    //     });
    // }

    isChecked(e) {
        this.setState({
            checked : this.state.checked.concat(e.target.value)
        });
        console.log(this.state)
        console.log(e.target.value);
        this.props.onChecked(this.state)
    }

    render() {
        return (
            <span className = "list">
                <input 
                    type = "checkbox"
                    value = {this.props.name}
                    onClick = {this.isChecked} />
                <label>{this.props.name}</label>

            </span>
        );
    }
}

export default List
