import React, {Component} from "react";
import Lists from "./Lists";
import Header from "./Header"
import Articles from "./Articles";

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            information : []
        }
        // this.handleSubmit = this.handleSubmit.bind(this);
        this.handleCreate = this.handleCreate.bind(this);
    }

    // 안쓰는중
    handleCreate(data) {
        this.setState({
            information : this.state.information.concat(data)
        });

        setTimeout(() => {
            console.log("456", this.state);
        }, 0)
    }

    renderList = () => {
        const newLists = this.state.newsLists.map((element, index) => {
          return (
            <div key = {index} className = "individualNewsList">
              <div>
                {element.title}
              </div>
              <div>
                {element.source.name} 
              </div>
              <div>
                {element.author}
              </div>
              <div>
                {element.publishedAt}
              </div>
            </div>);
        });

        return newLists;
    }



    render() {
        return (
            <div>
                <Header onTest = {this.handleCreate}/>
                <Articles onRender = {this.renderList}/>
                {/* {JSON.stringify(this.state.information)} */}
                {/* <Lists onCreate = {this.handleCreate} /> */}
            </div>
        );
    }
}

export default Page;
