import React, {Component} from "react";
import "./Lists.css";
import List from "./List";

// function List(){
//     return (
//         <span className = "list">
//             <input type = "checkbox" value = {this.props.name} ame = "isChecked" onChange = {this.handleCheckedd />
//             <label>{this.props.name}</label>
//         </span>
//     );
// }

class Lists extends Component {
    state = {

    };

    async componentDidMount() {
        // 컴포넌트 내부에서만 쓰는거 => state;
        // 상위컴포넌트가 하위컴포넌트로 내랴주는게 => props;
        const res = await this._makeList();
        await this.asdasd(res.id);

    }

    _renderList = () => { 
        const lists = this.state.lists.map((element, index) => {
            return <List name = {element.name} key = {index} />
        // 리턴 다음이랑 아래랑 같잖아. 굳이 이렇게 멀리갈 필요x 고작 10줄 늘어나도ㅗ딤.

        //     <span className = "list">
        //     {/* <form className = "publisherList"> */}
        //         <input
        //             type = "checkbox"
        //             value = {this.props.name}
        //             name = "isChecked"
        //             onChange = {this.handleCheckedd
        //         />
        //         <label>{this.props.name}</label>
        //     {/* </form> */}
        // </span>
        });
        return lists;
    }

    _makeList = async () => {
        const lists = await this._callApi();
        this.setState({
            lists
        });
    }

    _callApi = async () => {
        try{
            const response = await fetch(`https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c`);
            return (response.json()).sources;
        } catch(error) {
            return error;            
        };
        // return fetch(`https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
        // .then(response => response.json())
        // .then(json => json.sources)
        // .catch(err => console.log(err))
    }

    handleSubmit = (e) => {
        e.preventDefault();
        // this.props.onCheckedPublisher(this.state);
    }

    render() {
        const { lists } = this.state; // 축약가능 es6

        return (
            <div className = "lists">
                <form onSubmit = {this.handleSubmit}>
                    {this.state.lists ? this._renderList() : "Loading"}
                    <button type = "submit">등록</button>
                </form>

            </div>
        );
    }
}

export default Lists;
