import React, { Component } from 'react';
import "./View.css"

class View extends Component {
    constructor(props) {
        super(props);
        this.state = {
            viewStatus : false
        };

        this.toggleView = this.toggleView.bind(this);
    }

    toggleView() {
        const currentViewStatus = this.state.viewStatus;
        this.setState({
            viewStatus : !currentViewStatus
        })
    }

    render() {
        return (
            <div className = "select-view">
                {!this.state.viewStatus ? <i className = "fa fa-ellipsis-v" onClick = {this.props.onTest} onToggle = {this.toggleView()}></i> : <i className = "fa fa-ellipsis-h" onClick = {this.props.onTest} onToggle = {this.toggleView()}></i>}
                {/* <i className = "fa fa-ellipsis-v" onClick = {this.props.onTest}></i> */}
                {/* <i className = "fa fa-ellipsis-h"></i> */}
            </div>
        );
    }
}

export default View;
