import React, { Component } from "react";
import "./header.css";

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            periodStart: "",
            periodEnd: "",
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({
            periodStart : document.querySelector(".periodStart").value,
            // periodEnd : document.querySelector(".periodEnd").value
        });
        // this.setState({periodEnd : event.target.value});
    }

    handleSubmit(event) {
        console.log(this.state.periodStart,this.state.perirodEnd);
        event.preventDefault();
    }

    render() {
        return (
            <div className = "header">
                <input type = "text" className = "keywordBox" placeholder = "keyword"></input>

                <form className = "period" onSubmit = {this.handleSubmit}>
                    <label>
                        <input type = "date" className = "periodStart" value = {this.state.periodStart} onChange = {this.handleChange} />
                        {/* <input type = "date" className = "periodEnd" value = {this.state.periodEnd}></input> */}
                    </label>
                    <input type = "submit" value = "Submit"></input>
                </form>

            </div>
        );
    }
}

export default Header
