import React, {Component} from "react";
import "./Header.css";

class Header extends Component {
  //밑의 input태그의 this.handleChange를 this.props.handleChange같은 구조로.
  //따로 state없이도 부모(page)에서 가져온 props만으로도 조작가능하니깐.
  constructor(props) {
    super(props);
    this.state = {
      articles : [],
    };

    this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    this.setState({
      [e.target.name] : e.target.value
    });
  }

  // handleSubmit(e) {
  //   e.preventDefault();
  //   this.makeList();
  // }

  // makeList = async () => {
  //   const newsLists = await this.callNewsApi();
  //   this.setState({
  //       newsLists
  //   });
  //   console.log(this.state);
  // }

  renderList = () => {
    const newLists = this.state.newsLists.map((element, index) => {
      return (
        <div key = {index} className = "individualNewsList">
          <div>
            {element.title}
          </div>
          <div>
            {element.source.name} 
          </div>
          <div>
            {element.author}
          </div>
          <div>
            {element.publishedAt}
          </div>
        </div>);
    });

    return newLists;
  }

  // callNewsApi = () => {
  //   return fetch(`https://newsapi.org/v2/everything?q=${this.state.searchKeyword}&from=${this.state.periodStart}&to=${this.state.peroidEnd}&sortBy=popularity&pageSize=30&page=1&apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
  //   .then(response => response.json())
  //   .then(json => json.articles)
  //   .catch(err => console.log(err));
  // }

  render() {
    return (
      <div className = "header">
        <form className = "period" onSubmit = {this.props.onTest}>

          <label>
            <input type = "text" className = "searchKeyword" name = "searchKeyword" placeholder = "keyword" onChange = {this.handleChange}></input>

            <input type = "date" name = "periodStart" className = "periodStart" onChange = {this.handleChange}/>

            <input type = "date" name = "periodEnd" className = "periodEnd" onChange = {this.handleChange}/>

            <input type = "submit" className = "submitBtn"/>
          </label>
        </form>

        <div>
          {this.state.newsLists ? this.renderList() : ""}
        </div>
      </div>
    );
  }
}

export default Header
