import React, { Component } from "react";
import "./List.css";

class List extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isChecked : false,
            length : 0
        };

        this.test = this.handleChecked.bind(this);
        this.test = this.handleSubmit.bind(this);
    }

    handleChecked(event) {
        const value = event.target.type === "checkbox" ? event.target.checked : event.target.value;
        console.log(event.target.value);
        console.log(value);

        this.setState({
            [event.target.name] : value
        });
    }

    render() {
        return (
            <span className = "list">
                <input type = "checkbox" className = "isChecked" value = {this.props.name} name = "isChecked" onChange = {this.handleChecked}/><label>{this.props.name}</label>
            </span>
        );
    }
}

export default List
