import React, {Component} from "react";
import "./Lists.css";
import List from "./List";

class Lists extends Component {
    state = {};

    componentDidMount() {
        this.makeList();
    }

    renderList = () => {
        const lists = this.state.lists.map((element, index) => {
            return <List name = {element.name} key = {index} />
        });
        return lists;
    }

    makeList = async () => {
        const lists = await this.callPublisherApi();
        this.setState({
            lists
        });
    }

    callPublisherApi = () => {
        return fetch("https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c")
        .then(response => response.json())
        .then(json => json.sources)
        .catch(err => console.log(err));
    }

    render() {
        return (
            <div className = "Lists">
                {this.state.lists ? this.renderList() : "Loading"}
            </div>
        );
    }

    state = {

    };

    async componentDidMount() {
        this._makeList();
    }

    _renderList = () => { 
        const lists = this.state.lists.map((element, index) => {
            return <List name = {element.name} key = {index} />
        // 리턴 다음이랑 아래랑 같잖아. 굳이 이렇게 멀리갈 필요x 고작 10줄 늘어나도ㅗ딤.

        //     <span className = "list" />
        //         <input
        //             type = "checkbox"
        //             value = {this.props.name}
        //             name = "isChecked"
        //             onChange = {this.handleCheckedd
        //         />
        //         <label>{this.props.name}</label>
        // </span>
        });
        return lists;
    }

    _makeList = async () => {
        const lists = await this._callApi();
        this.setState({
            lists
        });
    }

    _callApi = async () => {
        try {
            const response = await fetch(`https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c`);
            return response;
        } catch(error) {
            return error;            
        };


        // return fetch(`https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
        // .then(response => response.json())
        // .then(json => json.sources)
        // .catch(err => console.log(err))
    }

    handleSubmit = (e) => {
        e.preventDefault();
        // this.props.onCheckedPublisher(this.state);
    }

    render() {
        return (
            <div className = "lists">
                <form onSubmit = {this.handleSubmit}>
                    {this.state.lists ? this._renderList() : "Loading"}
                    <button type = "submit">등록</button>
                </form>

            </div>
        );
    }


}

export default Lists;
