import React, {Component} from "react";
import Header from "./Header"
import Articles from "./Articles";
import View from "./View";

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            articleLists : [],
            clicked : false
        }
        this.handleCreate = this.handleCreate.bind(this);
        this.handleTest1 = this.handleTest1.bind(this);
        this.handleTest2 = this.handleTest2.bind(this);
    }

    handleCreate(articlesToAdd) {
        this.setState({
            articleLists : this.state.articleLists.concat(articlesToAdd)
        });
    }

    handleTest1() {
        this.setState({
            clicked : true
        });
    }

    handleTest2() {
        this.setState({
            clicked : true
        });
    }

    render() {
        let className = this.state.clicked === false ? "test1" : "test2"
        return (
            <div>
                <Header onChangePageState = {this.handleCreate}/>
                {/* view를 이렇게 컴포넌틐로 해도되지만.. 정보가 왔다갔다하는게 어려우면 그냥 여기 때려박아도 될듯한데 */}
                <View onTest = {this.test}/>
                <Articles getArticleLists = {this.state.articleLists}/>

                <div className = {className} onClick = 
                {this.handleTest}>연습용</div>
            </div>
        );
    }
}

export default Page;
