import React, {Component} from "react";
import "./Header.css";
import Lists from "./Lists";
import ModalPortal from "./ModalPortal";

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchKeyword : "",
            periodStart : "",
            periodEnd : "",
            publisherLists : [],
            modal : false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getPublisher = this.getPublisher.bind(this);
    this.handleOpenModal = this.handleOpenModal.bind(this);
    this.handleCloseModal = this.handleCloseModal.bind(this);
    }

    handleChange(e) {
        this.setState({
        [e.target.name] : e.target.value
        });
    }

    handleSubmit(e) {
        e.preventDefault();
        this.searchNewArticlesQualificateConditions();
    }

    searchNewArticlesQualificateConditions = async () => {
        const newArticles = await this.callApiToSearchArticles();
        this.props.onChangePageState(newArticles);
    }

    getPublisher(data) {
        this.setState({
        publisherLists : this.state.publisherLists.concat(data)
        });
    }

    callApiToSearchArticles = () => {
        const makeUrl = this.state.publisherLists.join(",");

        return fetch(`https://newsapi.org/v2/everything?q=${this.state.searchKeyword}&sources=`+`${makeUrl}`+`&from=${this.state.periodStart}&to=${this.state.peroidEnd}&sortBy=popularity&pageSize=30&page=1&apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
        .then(response => response.json())
        .then(json => json.articles)
        .catch(err => console.log(err));
    }

    handleOpenModal() {
        this.setState({
            modal : true
        })
    }

    handleCloseModal() {
        this.setState({
            modal : false
        })
    }

    render() {
        return (
        <div className = "header">
            <div className = "title">
                <h1>NEWS</h1>
            </div>
            <div className = "navigation">
                <button onClick={this.handleOpenModal} class = "navigation-publisher">신문사 선택</button>
                <form onSubmit = {this.handleSubmit}>
                    <span className = "navigation-period">
                        <input type = "date" name = "periodStart" className = "periodStart" onChange = {this.handleChange}/>
                        <input type = "date" name = "periodEnd" className = "periodEnd" onChange = {this.handleChange}/>
                    </span>
                    <span className = "navigation-keyword">
                        <input type = "text" className = "searchKeyword" name = "searchKeyword" placeholder = "keyword" onChange = {this.handleChange}></input>
                    </span>
                    <span className = "navigation-submit">
                        <label>
                        <i class="fa fa-arrow-circle-right"></i>
                        </label>
                            <input onClick = {this.handleSumbit} type = "submit" className = "submit-button" />


                    </span>
                </form>
            </div>



            {this.state.modal && (
                <ModalPortal>
                    <Lists onGetPublisher = {this.getPublisher} onClose={this.handleCloseModal}/>
                </ModalPortal>
            )}
        </div>
        );
    }
}

export default Header
