import React, {Component} from "react";
import "./Lists.css";
import List from "./List";

class Lists extends Component {
    constructor(props) {
        super(props);

        this.state = {
            checked : []
        };

        this.callPublisherApi = this.callPublisherApi.bind(this);
        this.renderList = this.renderList.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChecked = this.handleChecked.bind(this);
        this.transportPublisherToFather = this.transportPublisherToFather.bind(this);
    }

    componentDidMount() {
        this.makeList();
    }

    makeList = async () => {
        const lists = await this.callPublisherApi();
        this.setState({
            lists
        });
    }

    callPublisherApi() {
        return fetch("https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c")
        .then(response => response.json())
        .then(json => json.sources)
        .catch(err => console.log(err));
    }

    renderList() {
        const lists = this.state.lists.map((element, index) => {
            return <List onChecked = {this.handleChecked} name = {element.name} id = {element.id} key = {index} />
        });

        return lists;
    }

    handleSubmit(e) {
        e.preventDefault();
    }

    handleChecked(data) {
        this.setState({
            checked : this.state.checked.concat(data)
        })
    }

    transportPublisherToFather() {
        this.props.onGetPublisher(this.state.checked);
        this.props.onClose();
    }

    render() {
        return (
            <div className="MyModal">
                <div className="content">
                    <div>
                        <form onSubmit = {this.handleSubmit} >
                            <button type = "submit" className = "button" onClick = {this.transportPublisherToFather}>등록</button>
                            <div className = "lists">
                                {this.renderList()}
                                {/* {this.state.lists ? this.renderList() : <span className = "loading">"Loading"</span>} */}
                            </div>
                        </form>
                    </div>
                    <button onClick = {this.props.onClose} className = "button">닫기</button>
                </div>
            </div>
        );
    }
}

export default Lists;
