import React, {Component} from "react";
import Header from "./Header"
import Articles from "./Articles";
import View from "./View";

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            articleLists : [],
            clicked : false,
            viewStatus : true
        }
        this.handleCreate = this.handleCreate.bind(this);
        this.toggleClass = this.toggleClass.bind(this);
        this.updateViewStatus = this.updateViewStatus.bind(this);
    }

    updateViewStatus(data) {
        console.log("지금 viesStatus는 : ", data);
        this.setState({
            viewState : data
        });
        setTimeout(() => {
            console.log("변화를 주고나면!", this.state.viewStatus);
        },0);
    }

    handleCreate(articlesToAdd) {
        this.setState({
            articleLists : this.state.articleLists.concat(articlesToAdd)
        });
    }

    toggleClass() {
        const currentState = this.state.clicked;
        this.setState({
            clicked : !currentState
        });
    }

    render() {
        return (
            <div>
                <Header onChangePageState = {this.handleCreate}/>
                {/* view를 이렇게 컴포넌틐로 해도되지만.. 정보가 왔다갔다하는게 어려우면 그냥 여기 때려박아도 될듯한데 */}
                <View onToggleClass = {this.toggleClass} onUpdataViewStatus = {this.updateViewStatus}/>

                {/* 테스트로 실험을 해보니 <Articles />에게는 바로 className을 줄 수 없네. 저 안으로 들어가서 className = {this.state.clicked ? .... } 이걸 넣어줘야함 */}

                <Articles getArticleLists = {this.state.articleLists} onData = {this.state.clicked}/>

                {/* <div className = {this.state.clicked ? "colorBlue" : "colorRed"}>연습용</div>

                <Test /> */}
            </div>
        );
    }
}

export default Page;
