import React, {Component} from "react";
import Lists from "./Lists";
import Header from "./Header"

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            information : []
        }

        // this.handleCreate = this.handleCreate.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    // 안쓰는중
    // handleCreate(data) {
    //     console.log("123,", data);
    //     this.setState({
    //         information : this.state.information.concat(data)
    //     });
    // }

    handleChange(e) {
        this.setState({
          [e.target.name] : e.target.value
        });
    }

    handleSubmit(e) {
        e.preventDefault();
        this.makeList();
    }

    makeList = async () => {
        const newsLists = await this.callNewsApi();
        this.setState({
            newsLists
        });
        console.log(this.state);
    }

    callNewsApi() {
        return fetch(`https://newsapi.org/v2/everything?q=${this.state.searchKeyword}&from=${this.state.periodStart}&to=${this.state.peroidEnd}&sortBy=popularity&pageSize=30&page=1&apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
        .then(response => response.json())
        .then(json => json.articles)
        .catch(err => console.log(err));
    }

    // handleCreate = (data) => {
    //     console.log("123", data);
    //     this.setState({
    //         information: this.state.information.concat(data)
    //     });
    // }

    render() {
        return (
            <div>
                <Header onTest = {this.handleSubmit} onTest2 = {this.handleChange}/>
                {/* {JSON.stringify(this.state.information)} */}
                {/* <Lists onCreate = {this.handleCreate} /> */}
            </div>
        );
    }
}

export default Page;


