class Lists extends Component {
    constructor(props) {
        super(props);
        state = {
            number : 0
        };

        this.handleIncrease = this.handleIncrease.bind(this);
        this.handleDecrease = this.handleDecrease.bind(this);
    }



    handleIncrease() {
        this.setState({
            number : this.state.number + 1
        })
    }

    handleDecrease() {
        this.setState({
            number : this.state.number - 1
        })
    }

    render() {
        return (
            <div>
                <h1>
                    카운터
