import React, {Component} from "react";
import "./Header.css";

class Header extends Component {
  //밑의 input태그의 this.handleChange를 this.props.handleChange같은 구조로.
  //따로 state없이도 부모(page)에서 가져온 props만으로도 조작가능하니깐.

  constructor(props) {
    super(props);
    this.state = {

    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

  }

  handleChange(e) {
    this.setState({
      [e.target.name] : e.target.value
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    console.log(this.state);
    this.makeList();
  }

  makeList = async () => {
      const lists = await this.callPublisherApi();
      this.setState({
          lists
      });
  }

  callPublisherApi = () => {
      return fetch(`https://newsapi.org/v2/everything?q=bitcoin&apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
      .then(response => response.json())
      .then(json => json.sources)
      .catch(err => console.log(err));
  }

  render() {
    return (
      <div className = "header">
        <form className = "period" onSubmit = {this.handleSubmit}>

          <label>
            <input type = "text" className = "searchKeyword" name = "searchKeyword" placeholder = "keyword" onChange = {this.handleChange}></input>

            <input type = "date" name = "periodStart" className = "periodStart" onChange = {this.handleChange}/>

            <input type = "date" name = "periodEnd" className = "periodEnd" onChange = {this.handleChange}/>

            <input type = "submit" className = "submitBtn"/>
          </label>
        </form>
      </div>
    );
  }
}

export default Header
