import React, { Component } from 'react';

class Articles extends Component {
    constructor(props) {
        super(props);
        this.state = {

        };
        this.renderList = this.renderList.bind(this);
    }

    renderList = () => {
        const newLists = this.props.data.map((element, index) => {
            return (
                <div key = {index} className = "individualNewsList">
                    <div>{element.title}</div>
                    <div>{element.source.name} </div>
                    <div>{element.author}</div>
                    <div>{element.publishedAt}</div>
                </div>);
            });
        return newLists;
    }

    makeList = async () => {
        const newsLists = await this.callNewsApi();
        this.props.onTest(newsLists);

        //밑에 지운이유 : newsLists를 page.js로 보내고있는데 굳이 header.js의 state에 저장할 필요가 있을까?? 없어서 지움.
        // this.setState({
        //     newsLists
        // });
    }

    //위와 마찬가지로, page에서 this.props.으로 데이터를 불러와서 api를 날린다면, 아래도 this.state가 필요없는것 아닐까?
    callNewsApi = () => {
        return fetch(`https://newsapi.org/v2/everything?q=${this.state.searchKeyword}&from=${this.state.periodStart}&to=${this.state.peroidEnd}&sortBy=popularity&pageSize=30&page=2&apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
        .then(response => response.json())
        .then(json => json.articles)
        .catch(err => console.log(err));
    }

    infScroll() {
        makeList();
    }

    render() {
        return (
            <div>
                jun
                {this.renderList()}
                <button onClick = {this.infScroll}>스크롤!</button>
            </div>
        );
    }
}

export default Articles;
