import React, {Component} from "react";
import "./Header.css";
import Lists from "./Lists";

import MyModal from './MyModal';
import ModalPortal from "./ModalPortal";

class Header extends Component {
    //밑의 input태그의 this.handleChange를 this.props.handleChange같은 구조로.
    //따로 state없이도 부모(page)에서 가져온 props만으로도 조작가능하니깐.
    constructor(props) {
        super(props);
        this.state = {
            searchKeyword : "",
            periodStart : "",
            periodEnd : "",
            publisherLists : [],
            modal : false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getPublisher = this.getPublisher.bind(this);

    this.handleOpenModal = this.handleOpenModal.bind(this);
    this.handleCloseModal = this.handleCloseModal.bind(this);
    }

    handleChange(e) {
        this.setState({
        [e.target.name] : e.target.value
        });
    }

    handleSubmit(e) {
        e.preventDefault();
        this.searchNewArticlesQualificateConditions();
    }

    searchNewArticlesQualificateConditions = async () => {
        const newArticles = await this.callApiToSearchArticles();
        this.props.onChangePageState(newArticles);
    }

    getPublisher(data) {
        this.setState({
        publisherLists : this.state.publisherLists.concat(data)
        });
    }

    //위와 마찬가지로, page에서 this.props.으로 데이터를 불러와서 api를 날린다면, 아래도 this.state가 필요없는것 아닐까?
    callApiToSearchArticles = () => {
        const makeUrl = this.state.publisherLists.join(",");

        return fetch(`https://newsapi.org/v2/everything?q=${this.state.searchKeyword}&sources=`+`${makeUrl}`+`&from=${this.state.periodStart}&to=${this.state.peroidEnd}&sortBy=popularity&pageSize=30&page=1&apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
        .then(response => response.json())
        .then(json => json.articles)
        .catch(err => console.log(err));
    }

    handleOpenModal() {
        this.setState({
            modal : true
        })
    }

    handleCloseModal() {
        this.setState({
            modal : false
        })
    }

    render() {
        return (
        <div className = "header">
            <form className = "period" onSubmit = {this.handleSubmit}>
                <input type = "text" className = "searchKeyword" name = "searchKeyword" placeholder = "keyword" onChange = {this.handleChange}></input>
                <input type = "date" name = "periodStart" className = "periodStart" onChange = {this.handleChange}/>
                <input type = "date" name = "periodEnd" className = "periodEnd" onChange = {this.handleChange}/>
                <input type = "submit" className = "submitBtn"/>
            </form>

            <button onClick={this.handleOpenModal}>모달 열기</button>
            {this.state.modal && (
                <ModalPortal>
                    <MyModal onClose={this.handleCloseModal}/>
                </ModalPortal>
            )}

            <div className = "pubLists">
                <Lists onGetPublisher = {this.getPublisher}/>
            </div>
        </div>
        );
    }
}

export default Header
