import React, {Component} from "react";
import "./App.css";
import Header from "./header";

class App extends Component {

    state = {};

    componentDidMount() {
        fetch("https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c")
        .then(response => console.log(response))
        .catch(err => console.log(err))
    }

    _renderList = () => {
        const lists = this.state.arr.map((element, index) => {
            return <Header title = {element.title} key = {index} />
        });
        return lists;
    }

    render() {
        return (
            <div className = "App">
                {this.state.arr ? this._renderList() : "Loading"}
            </div>
        );
    }
}

export default App;

