import React, {Component} from "react";
import Header from "./Header"
import View from "./View";
import Modal from "./Modal";

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            articleLists : [],
            clicked : false,
            viewStatus : null,
            currentArticle : "",
            modal : false,
            reloadStatus : false,
            page : 1
        }

        this.handleCreate = this.handleCreate.bind(this);
        this.toggleClass = this.toggleClass.bind(this);
        this.updateViewStatus = this.updateViewStatus.bind(this);
        this.makeJsxListView = this.makeJsxListView.bind(this);
        this.makeJsxCardView = this.makeJsxCardView.bind(this);
        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
        this.changeModalStatusWhenClickOutside = this.changeModalStatusWhenClickOutside.bind(this);
        this.resetArticleLists = this.resetArticleLists.bind(this);
    }

    resetArticleLists() {
        this.setState({
            articleLists : []
        })
    }

    changeModalStatusWhenClickOutside(data) {
        this.setState({
            modal : data
        })
    }

    handleOpenModal(data) {
        this.setState({
            modal : true,
            currentArticle : data
        })
    }

    handleCloseModal() {
        this.setState({
            modal : false
        })
    }

    updateViewStatus(viewStatusFromViewComponent) {
        this.setState({
            viewStatus : viewStatusFromViewComponent
        });
    }

    handleCreate(articlesToAdd) {
        this.setState({
            articleLists : this.state.articleLists.concat(articlesToAdd)
        });
    }

    toggleClass() {
        const currentState = this.state.clicked;
        this.setState({
            clicked : !currentState
        });
    }

    makeJsxListView() {
        const newArticle = this.state.articleLists.map((element, index) => {
            return (
                <div key = {index} className = "newArticle" onClick = {() => {
                    this.handleOpenModal(element);
                }}>
                    <div className = "listViewTitle">{element.title}</div>
                    <div className = "listViewSource">{element.source.name}</div>
                    <div className = "listViewAuthor">{element.author}</div>
                    <div className = "ListViewPublisher">{element.publishedAt.slice(0, 10)}</div>
                </div>
            );
        });

        return newArticle;
    }

    // makeJsxCardView() {
    //     const newArticle = this.state.articleLists.map((element, index) => {
    //         return (
    //             <div key = {index} className = "newArticle" onClick = {() => {
    //                 this.handleOpenModal(element);
    //             }}>
    //                 {element.urlToImage ? <img src = {element.urlToImage} className = "articleImg"/> : <img src = "https://c-lj.gnst.jp/public/img/common/noimage.jpg?20181001050045" className = "articleImg"/>}
    //                 <div>{element.title}</div>
    //                 <div>{element.author}</div>
    //             </div>
    //         );
    //     });

    //     return newArticle;
    // }

    makeJsxCardView() {
        const newArticle = this.state.articleLists.map((element, index) => {
            return (
                <div key = {index} className = "container" onClick = {() => {
                    this.handleOpenModal(element);
                }}>
                    {element.urlToImage ? <img src = {element.urlToImage} className = "image"/> : <img src = "https://c-lj.gnst.jp/public/img/common/noimage.jpg?20181001050045" className = "image"/>}
                    <div className = "middle">
                        <div className = "card-view-title">{element.title}</div>
                        <div className = "card-view-author">{element.author}</div>
                    </div>
                </div>
            );
        });

        return newArticle;
    }

    render() {
        return (
            <div className = "structure" ref={node => { this.node = node; }}>
                <Header onChangePageState = {this.handleCreate} resetArticleLists = {this.resetArticleLists}/>
                <View onToggleClass = {this.toggleClass} onUpdataViewStatus = {this.updateViewStatus}/>

                <div className = {!this.state.viewStatus ? "view-list" : "view-card"}>

                    {!this.state.viewStatus ? this.makeJsxListView() : this.makeJsxCardView()}
                </div>

                {
                    this.state.modal && (
                        <Modal onClose = {this.handleCloseModal} modalData = {this.state.currentArticle} changeStateModalClickOutside = {this.changeModalStatusWhenClickOutside}/>
                    )
                }
            </div>
        );
    }
}

export default Page;
