import React, { Component } from 'react';
import "./View.css"

class View extends Component {
    constructor(props) {
        super(props);
        this.state = {
            clicked : false
        }
        this.toggleClass = this.toggleClass.bind(this);
    }

    toggleClass() {
        const currentState = this.state.clicked;
        this.setState({
            clicked : !currentState
        });
    }

    render() {
        return (
            <div className = "select-view">
                <i className = "fa fa-ellipsis-v" onClick = {this.toggleClass}></i>
                <i className = "fa fa-ellipsis-h"></i>


                <div className = {this.state.clicked ? "colorBlue" : "colorRed"} onClick = {this.toggleClass}>연습용</div>
            </div>
        );
    }
}

export default View;
