import React, { Component } from 'react';
import "./Modal.css";

class Modal extends Component {
    constructor() {
        super();

        this.handleClick = this.handleClick.bind(this);
        this.handleOutsideClick = this.handleOutsideClick.bind(this);

        this.state = {
          popupVisible: false
        };
    }

    handleClick() {
        if (!this.state.popupVisible) {
          document.addEventListener('click', this.handleOutsideClick, false);
        } else {
          document.removeEventListener('click', this.handleOutsideClick, false);
        }

        this.setState(prevState => ({
          popupVisible: !prevState.popupVisible,
        }));
    }

    handleOutsideClick(e) {
        // ignore clicks on the component itself
        if (this.node.contains(e.target)) {
          return;
        }

        this.handleClick();
    }


    render() {
        return (
            <div className = "articleModal" ref={node => { this.node = node; }}>
                <div className = "articleModalContent">
                    <div className = "modal-title">{this.props.modalData.title}</div>
                    <div className = "modal-sourceInfo">
                        <div>{this.props.modalData.author}</div>
                        <div>{this.props.modalData.publishedAt}</div>
                        <div>{this.props.modalData.source.name}</div>
                    </div>
                    <div className = "modal-content">
                        <a href = {this.props.modalData.url} target="_blank">{this.props.modalData.urlToImage ? <img src = {this.props.modalData.urlToImage} className = "articleImg"/> : <img src = "https://c-lj.gnst.jp/public/img/common/noimage.jpg?20181001050045" className = "articleImg"/>}
                        {this.props.modalData.content}</a>
                    </div>

                    <button onClick = {this.props.onClose} className = "button">닫기</button>
                </div>
            </div>
        );
    }
}

export default Modal;
