import React, {Component} from "react";
import "./Header.css";
import Lists from "./Lists";

class Header extends Component {
  //밑의 input태그의 this.handleChange를 this.props.handleChange같은 구조로.
  //따로 state없이도 부모(page)에서 가져온 props만으로도 조작가능하니깐.
  constructor(props) {
    super(props);
    this.state = {
      searchKeyword : "",
      periodStart : "",
      periodEnd : "",
      publisher : []
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getPublisher = this.getPublisher.bind(this);
  }

  handleChange(e) {
    this.setState({
      [e.target.name] : e.target.value
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    this.makeList();
  }

  makeList = async () => {
    const newsLists = await this.callNewsApi();
    this.props.onTest(newsLists);

    //밑에 지운이유 : newsLists를 page.js로 보내고있는데 굳이 header.js의 state에 저장할 필요가 있을까?? 없어서 지움.
    // this.setState({
    //     newsLists
    // });
  }

  getPublisher(data) {
    console.log("header에서 받았음.", data);
    this.setState({
      publisher : this.state.publisher.concat(data)
    });
    setTimeout(() => {
      console.log("받고 또받음", this.state.publisher[0]);
    },0);
  }

  //위와 마찬가지로, page에서 this.props.으로 데이터를 불러와서 api를 날린다면, 아래도 this.state가 필요없는것 아닐까?
  callNewsApi = () => {
    return fetch(`https://newsapi.org/v2/everything?q=${this.state.searchKeyword}&sources=abc-news-au&from=${this.state.periodStart}&to=${this.state.peroidEnd}&sortBy=popularity&pageSize=30&page=1&apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
    .then(response => response.json())
    .then(json => json.articles)
    .catch(err => console.log(err));
  }

  render() {
    return (
      <div className = "header">
        <form className = "period" onSubmit = {this.handleSubmit}>
          <label>
            <input type = "text" className = "searchKeyword" name = "searchKeyword" placeholder = "keyword" onChange = {this.handleChange}></input>
            <input type = "date" name = "periodStart" className = "periodStart" onChange = {this.handleChange}/>
            <input type = "date" name = "periodEnd" className = "periodEnd" onChange = {this.handleChange}/>
            <input type = "submit" className = "submitBtn"/>
          </label>
        </form>
        <Lists onGetPublisher = {this.getPublisher}/>
      </div>
    );
  }
}

export default Header
