import React, {Component} from "react";
import "./App.css";
import Header from "./header";

class App extends Component {
    componentDidMount() {
        this._makeSourcesList();

        // setTimeout(() => {
        //     this.setState({
        //         greeting : "bless you"
        //     });
        // }, 3000);
    }

    state = {
        greeting : "hello junwoo!?"
    };

    _makeSourcesList = async () => {
        const lists = await this._callApi();
        this.setState({
            lists
        });
    }

    _callApi = () => {
        return fetch("https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c")
        .then(data => data.json())
        .then(json => console.log(json.sources[0]))
        .catch(err => console.log(err));
    }

    render() {
        return (
            <div className = "App">
                {this.state.greeting}
                {this.state.lists}
                <Header />
            </div>
        );
    }
}

export default App;
