import React, { Component } from 'react';
import "./Modal.css";

class Modal extends Component {
    render() {
        return (
            <div className = "articleModal">
                <div className = "articleModalContent">
                    <div>{this.props.modalData.title}</div>
                    <div>{this.props.modalData.author}</div>
                    <div>{this.props.modalData.publishedAt}</div>
                    <div>
                        {this.props.modalData.urlToImage ? <img src = {this.props.modalData.urlToImage} className = "articleImg"/> : <img src = "https://c-lj.gnst.jp/public/img/common/noimage.jpg?20181001050045" className = "articleImg"/>}
                        {this.props.modalData.content}
                    </div>

                    <a href = {this.props.modalData.urlToImage}>뉴스 바로가기</a>

                    <button onClick = {this.props.onClose} className = "button">닫기</button>
                </div>
            </div>
        );
    }
}

export default Modal;
