import React, {Component} from "react";
import "./App.css";
import Header from "./header";

class App extends Component {

    state = {
        greeting : "hello!",
        arr : [
            {
            title : "junwoo"
            },
            {
                title : "gaon"
            },
            {
                title : "bless"
            }
        ]
    }

    componentDidMount() {
    }

    render() {
        return (
            <div className = "App">
                {this.state.greeting}
                {this.state.arr.map((element, index) => {
                    return <Header title = {element.title} key = {index} />
                })}
            </div>
        );
    }
}

export default App;

