import React, { Component } from "react";
import "./header.css";

class Header extends Component {

    constructor(props) {
        super(props);
        this.state = {value: ''};

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    handleChange(event) {
        this.setState({value: event.target.value});
    }

    render() {
        return (
            <div className = "header">
                <input type = "text" className = "keywordBox" placeholder = "keyword"></input>
                <form className = "period">
                    <label>
                        <input type = "date" className = "periodStart" value = {this.state.value} onChange = {this.handleChange} />
                        {/* <input type = "date" className = "periodEnd" value = {this.state.periodEnd}></input> */}
                    </label>

                </form>
            </div>
        );
    }
}

export default Header
