import React, {Component} from "react";
import "./App.css";
import Header from "./header";

const arr = [{
        title : "junwoo"
    },
    {
        title : "gaon"
    },
    {
        title : "bless"
    }
];

class App extends Component {
    render() {
        return (
            <div className = "App">
                {arr.map((element, index) => {
                    return <Header title = {element.title} key = {index} />
                })}
            </div>
        );
    }
}

export default App;

