import React, {Component} from "react";
import "./App.css";
import Header from "./header";

const arr = [];

class App extends Component {
    componentDidMount() {
        fetch("https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c")
        .then(data => data.json())
        .then(json => json.sources.map(data => {
            arr.push(data.id);
        }))
        .then(console.log("arr : ", arr))
        .catch(err => console.log(err));
    }

    render() {
        return (
            <div className = "App">
                <Header title = {arr[0]}/>
            </div>
        );
    }
}

export default App;

