import React, {Component} from "react";
import "./Header.css";
import Lists from "./Lists";
import ModalPortal from "./ModalPortal";

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchKeyword : "",
            periodStart : "",
            periodEnd : "",
            publisherLists : [],
            modal : false,
            page : 1,
            reloadStatus: false
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.getPublisher = this.getPublisher.bind(this);
        this.handleOpenModal = this.handleOpenModal.bind(this);
        this.handleCloseModal = this.handleCloseModal.bind(this);
        this.clickOutsideModal = this.clickOutsideModal.bind(this);
        this.resetPageStatus = this.resetPageStatus.bind(this);
        this.handleScroll = this.handleScroll.bind(this);
    }

    clickOutsideModal(modalStatus) {
        this.setState({
            modal : modalStatus
        })
    }

    resetPageStatus() {
        this.setState({
            page : 1
        })

        this.props.resetArticleLists()
    }

    handleScroll() {
        const nearBottom = window.innerHeight + window.scrollY >= document.body.offsetHeight - 500;
        if(nearBottom && !this.state.reloadStatus) {
            this.setState({
                page : this.state.page + 1
            })
            this.searchNewArticlesQualificateConditions();
            this.setState({
                reloadStatus: true
            })
        }

    }

    componentDidMount() {
        window.addEventListener('scroll', this.handleScroll);
    }
    componentWillMount() {
        console.log("하위, will mount")
    }

    handleChange(e) {
        this.setState({
        [e.target.name] : e.target.value
        });
    }

    userFriendly = (e) => {
        e.target.value = "";
    }

    handleSubmit(e) {
        e.preventDefault();
        this.searchNewArticlesQualificateConditions();
    }

    searchNewArticlesQualificateConditions = async () => {
        const newArticles = await this.getArticles();
        this.props.onChangePageState(newArticles);
        this.setState({
            reloadStatus: false
        })
    }

    getPublisher(data) {
        this.setState({
            publisherLists : this.state.publisherLists.concat(data)
        });
    }

    getArticles = () => {
        const publisherArrToString = this.state.publisherLists.join(",");

        return fetch(`https://newsapi.org/v2/everything?q=${this.state.searchKeyword}&sources=`+`${publisherArrToString}`+`&from=${this.state.periodStart}&to=${this.state.peroidEnd}&sortBy=popularity&pageSize=30&page=${this.state.page}&apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
        .then(response => response.json())
        .then(json => json.articles)
        .catch(err => console.log(err));
    }

    handleOpenModal() {
        this.setState({
            modal : true
        })
    }

    handleCloseModal() {
        this.setState({
            modal : false
        })
    }

    render() {
        return (
        <div className = "header" >
            <div className = "title">
                <h1>Sinnonhyeon Times</h1>
            </div>
            <div className = "navigation">
                <button onClick={this.handleOpenModal} className = "navigation-publisher"><i className = "fa fa-list"></i></button>
                <form onSubmit = {this.handleSubmit}>
                    <span className = "navigation-period">
                        <input type = "date" name = "periodStart" className = "period-start" onChange = {this.handleChange}/>
                        <input type = "date" name = "periodEnd" className = "period-end" onChange = {this.handleChange}/>
                    </span>
                    <span className = "navigation-keyword">
                        <input type = "text" className = "searchKeyword" name = "searchKeyword" placeholder = "Search" onChange = {this.handleChange} onClick = {this.userFriendly}></input>
                    </span>
                    <span className = "navigation-submit">
                        <button type = "submit" className = "submit-button" onClick = {this.resetPageStatus}>
                            <i className = "fa fa-arrow-circle-right"></i>
                            검색
                        </button>
                    </span>
                </form>
            </div>

            {this.state.modal && (
                <ModalPortal>
                    <Lists onGetPublisher = {this.getPublisher} onClose={this.handleCloseModal} changeModalStatus = {this.clickOutsideModal}/>
                </ModalPortal>
            )}
        </div>);
    }
}

export default Header
