import React, { Component } from "react";
import "./header.css";

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            periodStart: "",
            periodEnd: "",
        };
        this.handleChangeStart = this.handleChangeStart.bind(this);
        this.handleChangeEnd = this.handleChangeEnd.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChangeStart() {
        this.setState({
            periodStart : document.querySelector(".periodStart").value
        });
    }

    handleChangeEnd() {
        this.setState({
            periodEnd : document.querySelector(".periodEnd").value
        });
    }

    handleSubmit(event) {
        console.log(this.state);
        console.log(this.state.periodStart, this.state.periodEnd);
        event.preventDefault();
    }

    render() {
        return (
            <div className = "header">
                <input type = "text" className = "keywordBox" placeholder = "keyword"></input>

                <form className = "period" onSubmit = {this.handleSubmit}>
                    <label>
                        <input type = "date" className = "periodStart" value = {this.state.periodStart} onChange = {this.handleChangeStart} />
                        <input type = "date" className = "periodEnd" value = {this.state.periodEnd} onChange = {this.handleChangeEnd}></input>
                    </label>
                    <input type = "submit" className = "submitBtn" value = "Submit"></input>
                </form>

            </div>
        );
    }
}

export default Header
