import React, {Component} from "react";
import Header from "./Header"
import View from "./View";
//
import Modal from "./Modal";

class Page extends Component {
    constructor(props) {
        super(props);
        this.state = {
            articleLists : [],
            clicked : false,
            viewStatus : null,
            currentArticle : "",
            popupVisible: false
        }
        this.handleCreate = this.handleCreate.bind(this);
        this.toggleClass = this.toggleClass.bind(this);
        this.updateViewStatus = this.updateViewStatus.bind(this);
        this.makeJsxListView = this.makeJsxListView.bind(this);
        this.makeJsxCardView = this.makeJsxCardView.bind(this);
        //
        // this.handleOpenModal = this.handleOpenModal.bind(this);
        // this.handleCloseModal = this.handleCloseModal.bind(this);

        this.handleClick = this.handleClick.bind(this);
        this.handleOutsideClick = this.handleOutsideClick.bind(this);
    }

    handleClick(element) {
        if (!this.state.popupVisible) {
          document.addEventListener('click', this.handleOutsideClick, false);
        } else {
          document.removeEventListener('click', this.handleOutsideClick, false);
        }

        this.setState(prevState => ({
          popupVisible: !prevState.popupVisible,
          currentArticle : element
        }));
    }

    handleOutsideClick(e) {
    // ignore clicks on the component itself
        if (this.node.contains(e.target)) {
            return;
        }

        this.handleClick();
    }

    // handleOpenModal(data) {
    //     this.setState({
    //         modal : true,
    //         currentArticle : data
    //     })
    // }

    // handleCloseModal() {
    //     this.setState({
    //         modal : false
    //     })
    // }

    updateViewStatus(viewStatusFromViewComponent) {
        this.setState({
            viewStatus : viewStatusFromViewComponent
        });
    }

    handleCreate(articlesToAdd) {
        this.setState({
            articleLists : this.state.articleLists.concat(articlesToAdd)
        });
    }

    toggleClass() {
        const currentState = this.state.clicked;
        this.setState({
            clicked : !currentState
        });
    }

    makeJsxListView() {
        const newArticle = this.state.articleLists.map((element, index) => {
            return (
                <div key = {index} className = "newArticle" onClick = {() => {
                    this.handleClick(element);
                }}>
                    <div>{element.title}</div>
                    <div>{element.source.name}</div>
                    <div>{element.author}</div>
                    <div>{element.publishedAt}</div>
                </div>
            );
        });

        return newArticle;
    }

    makeJsxCardView() {
        const newArticle = this.state.articleLists.map((element, index) => {
            return (
                <div key = {index} className = "newArticle" onClick = {() => {
                    this.handleOpenModal(element);
                }}>
                    {element.urlToImage ? <img src = {element.urlToImage} className = "articleImg"/> : <img src = "https://c-lj.gnst.jp/public/img/common/noimage.jpg?20181001050045" className = "articleImg"/>}
                    <div>{element.title}</div>
                    <div>{element.author}</div>
                </div>
            );
        });

        return newArticle;
    }

    render() {
        return (
            <div ref={node => { this.node = node; }}>
                <Header onChangePageState = {this.handleCreate}/>
                <View onToggleClass = {this.toggleClass} onUpdataViewStatus = {this.updateViewStatus}/>

                <div className = {!this.state.viewStatus ? "colorRed" : "colorBlue"}>
                    {!this.state.viewStatus ? this.makeJsxListView() : this.makeJsxCardView()}
                </div>



                {/* {this.state.modal && (
                    <div className="MyModal">
                        <div className="content">
                            <div>

                            </div>
                            <button onClick = {this.handleCloseModal} className = "button">닫기</button>
                        </div>
                    </div>
                )} */}

                {
                    this.state.modal && (
                        <Modal onClose = {this.handleCloseModal} modalData = {this.state.currentArticle}/>
                    )
                }

            </div>
        );
    }
}

export default Page;
