import React, {Component} from "react";
import "./Lists.css";
import List from "./List";

class Lists extends Component {
    state = {

    };

    componentDidMount() {
        this.makeList();
    }

    makeList = async () => {
        const lists = await this.callPublisherApi();
        this.setState({
            lists
        });
    }

    callPublisherApi = () => {
        return fetch("https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c")
        .then(response => response.json())
        .then(json => json.sources)
        .catch(err => console.log(err));
    }

    renderList = () => {
        const lists = this.state.lists.map((element, index) => {
            return <List name = {element.name} key = {index} onHandle = {this.handleInputChange}/>
        });
        return lists;
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.props.onCreate(this.state)
    }

    handleInputChange(event) {
        const target = event.target;
        const value = target.type === "checkbox" ? target.checked : target.value;
        const name = target.name;

        this.setState({
            [name] : value
        });
    }

    render() {
        return (
            <div className = "Lists">
                <form onSubmit = {this.handleSubmit} className = "lists">
                    <button type = "submit" className = "button">등록</button>
                    {this.state.lists ? this.renderList() : "Loading"}

                </form>
            </div>
        );
    }
}

export default Lists;
