import React, {Component} from "react";
import "./Header.css";
import Lists from "./Lists";
import ModalPortal from "./ModalPortal";

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            searchKeyword : "",
            periodStart : "",
            periodEnd : "",
            publisherLists : [],
            popupVisible: false
            // modal : false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.getPublisher = this.getPublisher.bind(this);
    // this.handleOpenModal = this.handleOpenModal.bind(this);
    // this.handleCloseModal = this.handleCloseModal.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.handleOutsideClick = this.handleOutsideClick.bind(this);
    }

    handleChange(e) {
        this.setState({
        [e.target.name] : e.target.value
        });
    }

    handleSubmit(e) {
        e.preventDefault();
        this.searchNewArticlesQualificateConditions();
    }

    searchNewArticlesQualificateConditions = async () => {
        const newArticles = await this.getArticles();
        this.props.onChangePageState(newArticles);
    }

    getPublisher(data) {
        this.setState({
            publisherLists : this.state.publisherLists.concat(data)
        });
    }

    getArticles = () => {
        const publisherArrToString = this.state.publisherLists.join(",");

        return fetch(`https://newsapi.org/v2/everything?q=${this.state.searchKeyword}&sources=`+`${publisherArrToString}`+`&from=${this.state.periodStart}&to=${this.state.peroidEnd}&sortBy=popularity&pageSize=30&page=1&apiKey=4460b53fe7a040b68124fc9ae2a3c84c`)
        .then(response => response.json())
        .then(json => json.articles)
        .catch(err => console.log(err));
    }

    // handleOpenModal() {
    //     this.setState({
    //         modal : true
    //     })
    // }

    // handleCloseModal() {
    //     this.setState({
    //         modal : false
    //     })
    // }
    handleClick() {
        if (!this.state.popupVisible) {
          // attach/remove event handler
          document.addEventListener("click", this.handleOutsideClick, false);
        } else {
          document.removeEventListener("click", this.handleOutsideClick, false);
        }

        this.setState(prevState => ({
          popupVisible: !prevState.popupVisible
        }));
      }

      handleOutsideClick(e) {
        // ignore clicks on the component itself
        if (this.node.contains(e.target)) {
          return;
        }

        this.handleClick();
      }

    render() {
        return (
        <div className = "header" >
            <div className = "title">
                <h1>Sinnonhyeon Times</h1>
            </div>
            <div className = "navigation">
                <button onClick={this.handleClick} className = "navigation-publisher"><i className = "fa fa-list"></i></button>
                <form onSubmit = {this.handleSubmit}>
                    <span className = "navigation-period">
                        <input type = "date" name = "periodStart" className = "period-start" onChange = {this.handleChange}/>
                        <input type = "date" name = "periodEnd" className = "period-end" onChange = {this.handleChange}/>
                    </span>
                    <span className = "navigation-keyword">
                        <input type = "text" className = "searchKeyword" name = "searchKeyword" placeholder = "Search" onChange = {this.handleChange}></input>
                    </span>
                    <span className = "navigation-submit">
                        <button type = "submit" className = "submit-button">
                            <i className = "fa fa-arrow-circle-right"></i>
                            검색
                        </button>
                    </span>
                </form>
            </div>

            {this.state.popupVisible && (
                <ModalPortal >
                <Lists onGetPublisher = {this.getPublisher} onClose={this.handleClick} ref={node => { this.node = node; }}/>
            </ModalPortal>
            )}

            {/* {this.state.modal && (
                <ModalPortal>
                    <Lists onGetPublisher = {this.getPublisher} onClose={this.handleCloseModal}/>
                </ModalPortal>
            )} */}
        </div>
        );
    }
}

export default Header
