import React, { Component } from "react";
import "./List.css";

class List extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isChecked : false
        }

        this.handleChecked = this.handleChecked.bind(this);
    }


    handleChecked(event) {
        const value = event.target.type === "checkbox" ? event.target.checked : event.target.value;

        const name = event.target.name;

        // console.log(event.target.value);
        // console.log(value);
        // console.log(name);
        // console.log(event.target);
        // console.log(event.target.name);

        this.setState({
            [name] : value
        });
        console.log(this.state);
    }


    render() {
        return (
            <span className = "list">
                <form className = "publisherList">
                    <input type = "checkbox" value = {this.props.name} name = "isChecked" onChange = {this.handleChecked}/><label>{this.props.name}</label>
                </form>
            </span>
        );
    }
}

export default List
