import React, {Component} from "react";
import "./Lists.css";
import List from "./List";

class Lists extends Component {
    constructor(props) {
        super(props);

        this.state = {
            checked : []
        };

        // this.test = this.test.bind(this);
        // this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChecked = this.handleChecked.bind(this);
        this.transportPublisherToFather = this.transportPublisherToFather.bind(this);
    }

    componentDidMount() {
        this.makeList();
    }

    makeList = async () => {
        const lists = await this.callPublisherApi();
        this.setState({
            lists
        });
    }

    callPublisherApi = () => {
        return fetch("https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c")
        .then(response => response.json())
        .then(json => json.sources)
        .catch(err => console.log(err));
    }

    renderList = () => {
        const lists = this.state.lists.map((element, index) => {
            return <List onChecked = {this.handleChecked} name = {element.name} key = {index} />
        });
        return lists;
    }

    handleSubmit = (e) => {
        e.preventDefault();
        // this.props.onCreate(this.state)
    }



    handleChecked(data) {
        this.setState({
            checked : this.state.checked.concat(data)
        })
        setTimeout(() => {
            console.log(this.state)
        }, 0);
    }

    transportPublisherToFather() {
        this.props.onGetPublisher(this.state.checked);
    }

    render() {
        return (
            <div>
                <form onSubmit = {this.handleSubmit} >
                    <button type = "submit" className = "button" onClick = {this.transportPublisherToFather}>등록</button>
                    <div className = "lists">
                        {this.state.lists ? this.renderList() : "Loading"}
                    </div>
                </form>
            </div>
        );
    }
}

export default Lists;
