/*
    소스리스트출력
*/

class App extends Component {

    state = {};

    componentDidMount() {
        this._makeList();
    }

    _renderList = () => {
        const lists = this.state.lists.map((element, index) => {
            return <Header name = {element.name} key = {index} />
        });
        return lists;
    }

    _makeList = async () => {
        const lists = await this._callApi();
        this.setState({
            lists
        });
    }

    _callApi = () => {
        return fetch("https://newsapi.org/v2/sources?apiKey=4460b53fe7a040b68124fc9ae2a3c84c")
        .then(response => response.json())
        .then(json => json.sources)
        .catch(err => console.log(err))
    }

    render() {
        return (
            <div className = "App">
                {this.state.lists ? this._renderList() : "Loading"}
            </div>
        );
    }
}

class Header extends Component {
    render() {
        return (
            <div>
                {this.props.name}
            </div>
        );
    }
}

export default Header


