import React, {Component} from "react";
import "./Header.css";

class Header extends Component {

  handleChange = (e) => {
    this.setState({
      [e.target.name] : e.target.value
    });
  }

  handleSubmit = (e) => {
    console.log(this.state);
    e.preventDefault();
  }

  render() {
    return (
      <div className = "header">
          <input type = "text" className = "keywordBox" placeholder = "keyword"></input>

          <form className = "period">
            <label>
                <input type = "date" name = "periodStart" className = "periodStart" onChange = {this.handleChange}/>
                <input type = "date" name = "periodEnd" className = "periodEnd" onChange = {this.handleChange}/>
                <input type = "submit" className = "submitBtn" onClick = {this.handleSubmit}/>
            </label>
          </form>
          <div className = "junwoo">
          </div>
      </div>
    );
  }
}

